parameters.inputSchema:
{
  "$schema": "https://json-schema.org/draft/2020-12/schema",
  "title": "SubTargetList",
  "type": "array",
  "items": {
    "type": "object",
    "required": ["Sub-Target Name", "Relevance"],
    "properties": {
      "Sub-Target Name": {
        "type": "string",
        "description": "The name of the entity (individual, organization, event, or location) that is a sub-target of interest."
      },
      "Relevance": {
        "type": "string",
        "description": "A brief explanation of why this sub-target is relevant or strategically important."
      }
    },
    "additionalProperties": false
  }
}
