// Extract and validate incoming WhatsApp message data
const incomingData = $input.first().json;

// Extract message details
const from = incomingData.body.from || '';
const message = incomingData.body.message || '';
const messageType = incomingData.body.messageType || 'text';
const timestamp = incomingData.body.timestamp || new Date().toISOString();
const businessName = incomingData.body.businessName || 'Your Business';

// Clean phone number for logging
const cleanPhone = from.replace('@c.us', '');

// Log incoming message
console.log(`ðŸ“¥ Processing message from ${cleanPhone}: ${message}`);

// Validate message
if (!message || message.trim() === '') {
  return [{
    json: {
      error: 'Empty message received',
      from: from,
      timestamp: timestamp
    }
  }];
}

// Skip if message is too long (over 1000 characters)
if (message.length > 1000) {
  return [{
    json: {
      error: 'Message too long',
      from: from,
      message: 'Sorry, your message is too long. Please keep it under 1000 characters.',
      timestamp: timestamp
    }
  }];
}

// Prepare data for next nodes
return [{
  json: {
    from: from,
    message: message.trim(),
    messageType: messageType,
    timestamp: timestamp,
    businessName: businessName,
    cleanPhone: cleanPhone
  }
}];

// Get message data from previous node (Process Message)
const messageData = $("Process Message").first().json;

// Get knowledge base content from the Google Docs node
const docData = $input.first().json;

let knowledgeBase = "";

// Check if the content exists and is a string
if (docData && typeof docData.content === "string") {
  knowledgeBase = docData.content;
}

// Clean up the knowledge base text (optional, but good practice)
knowledgeBase = knowledgeBase
  .replace(/\n\s*\n/g, "\n") // Remove multiple newlines
  .replace(/\s+/g, " ") // Replace multiple spaces with single space
  .trim();

console.log(`ðŸ“š Knowledge base loaded: ${knowledgeBase.length} characters`);

// Prepare data for next nodes, combining message and knowledge base
return [{
  json: {
    ...messageData,
    knowledgeBase: knowledgeBase
  }
}];

// Process Gemini AI response and prepare for WhatsApp
const messageData = $("Extract Knowledge").first().json;
const aiResponse = $input.first().json;

// Extract the AI response text
let responseText = "";

// Check if the AI response has an 'output' field
if (aiResponse && aiResponse.output) {
  responseText = aiResponse.output.trim();
} else if (aiResponse && aiResponse.text) {
  // Fallback for older Gemini node versions or different structures
  responseText = aiResponse.text.trim();
} else if (aiResponse && aiResponse.response) {
  // Another fallback
  responseText = aiResponse.response.trim();
} else {
  responseText = "Sorry, I\"m having trouble processing your request right now. Please try again later.";
}

// Ensure response is not too long for WhatsApp
if (responseText.length > 1500) {
  responseText = responseText.substring(0, 1450) + "... (continued in next message)";
}

// Log the response
console.log(`ðŸ¤– AI Response for ${messageData.cleanPhone}: ${responseText.substring(0, 100)}...`);

// Prepare response for WhatsApp bot
return [{
  json: {
    to: messageData.from,
    message: responseText,
    timestamp: new Date().toISOString(),
    originalMessage: messageData.message,
    businessName: messageData.businessName
  }
}];

