const rows = items; // allrows from Google Sheet

// Count the days since July 5, 2025
const startDate = new Date("2025-07-04");
const today = new Date();
const dayDiff = Math.floor((today - startDate) / (1000 * 60 * 60 * 24));

// Calculate today's keyword index
const index = dayDiff % rows.length;
const selectedKeyword = rows[index].json.Title || rows[index].json.Keyword || "Unknown";

return [
  {
    json: {
      index,
      keyword: selectedKeyword,
      date: today.toISOString()
    }
  }
];

