// Comprehensive supplier risk assessment algorithm
const supplier = $json;
const riskSettings = $node['Risk Settings'].json;

// Get financial health data
const financialData = $node['Get Financial Health'].json;
const newsData = $node['Monitor News & Events'].json;
const performanceData = $node['Get Performance Data'].json;

let totalRiskScore = 0;
let riskFactors = [];

// Financial Risk Assessment (35% weight)
let financialRisk = 0;
if (financialData.creditScore) {
  if (financialData.creditScore < 500) {
    financialRisk = 35;
    riskFactors.push('Poor credit score');
  } else if (financialData.creditScore < 700) {
    financialRisk = 20;
    riskFactors.push('Below average credit score');
  } else if (financialData.creditScore < 800) {
    financialRisk = 10;
  } else {
    financialRisk = 0;
  }
}

// Payment behavior risk
if (financialData.paymentDelays > 30) {
  financialRisk += 10;
  riskFactors.push('Payment delays detected');
}

totalRiskScore += financialRisk;

// Operational Risk Assessment (30% weight)
let operationalRisk = 0;
if (performanceData.avg_delivery < 85) {
  operationalRisk += 15;
  riskFactors.push('Poor delivery performance');
}

if (performanceData.avg_quality < 90) {
  operationalRisk += 10;
  riskFactors.push('Quality issues detected');
}

if (performanceData.late_count > 5) {
  operationalRisk += 5;
  riskFactors.push('Frequent late deliveries');
}

totalRiskScore += operationalRisk;

// News Sentiment Risk (15% weight)
let newsRisk = 0;
if (newsData.articles && newsData.articles.length > 0) {
  const negativeKeywords = ['bankruptcy', 'lawsuit', 'recall', 'investigation', 'fraud', 'scandal'];
  const negativeArticles = newsData.articles.filter(article => 
    negativeKeywords.some(keyword => 
      article.title.toLowerCase().includes(keyword) || 
      article.description.toLowerCase().includes(keyword)
    )
  );

  if (negativeArticles.length > 0) {
    newsRisk = Math.min(negativeArticles.length * 5, 15);
    riskFactors.push(`${negativeArticles.length} negative news articles`);
  }
}

totalRiskScore += newsRisk;

// Geopolitical Risk Assessment (10% weight)
let geopoliticalRisk = 0;
const highRiskCountries = ['China', 'Russia', 'Iran', 'North Korea'];
const mediumRiskCountries = ['Turkey', 'Venezuela', 'Myanmar'];

if (highRiskCountries.includes(supplier.country)) {
  geopoliticalRisk = 10;
  riskFactors.push('High geopolitical risk country');
} else if (mediumRiskCountries.includes(supplier.country)) {
  geopoliticalRisk = 5;
  riskFactors.push('Medium geopolitical risk country');
}

totalRiskScore += geopoliticalRisk;

// Criticality Multiplier (10% weight)
let criticalityRisk = 0;
if (supplier.criticality_level === 'critical') {
  criticalityRisk = 10;
} else if (supplier.criticality_level === 'high') {
  criticalityRisk = 7;
} else if (supplier.criticality_level === 'medium') {
  criticalityRisk = 3;
}

totalRiskScore += criticalityRisk;

// Determine risk level and action
let riskLevel = 'low';
let riskColor = '🟢';
let recommendedAction = 'monitor';

if (totalRiskScore >= riskSettings.criticalRiskThreshold) {
  riskLevel = 'critical';
  riskColor = '🔴';
  recommendedAction = 'emergency_protocol';
} else if (totalRiskScore >= riskSettings.highRiskThreshold) {
  riskLevel = 'high';
  riskColor = '🟠';
  recommendedAction = 'immediate_contact';
} else if (totalRiskScore >= riskSettings.mediumRiskThreshold) {
  riskLevel = 'medium';
  riskColor = '🟡';
  recommendedAction = 'enhanced_monitoring';
}

// Calculate potential impact
const potentialImpact = {
  financial: totalRiskScore * 1000, // Estimated cost impact
  operational: riskLevel === 'critical' ? 'severe' : riskLevel === 'high' ? 'moderate' : 'minimal',
  timeline: riskLevel === 'critical' ? 'immediate' : riskLevel === 'high' ? '1-3 days' : '1-2 weeks'
};

return {
  supplier_id: supplier.supplier_id,
  supplier_name: supplier.supplier_name,
  category: supplier.category,
  country: supplier.country,
  risk_score: Math.round(totalRiskScore),
  risk_level: riskLevel,
  risk_color: riskColor,
  recommended_action: recommendedAction,
  risk_factors: riskFactors,
  potential_impact: potentialImpact,
  financial_risk: financialRisk,
  operational_risk: operationalRisk,
  news_risk: newsRisk,
  geopolitical_risk: geopoliticalRisk,
  criticality_risk: criticalityRisk,
  assessed_at: new Date().toISOString()
};
