// Get the markdown content from the previous node
const markdownContent = $input.item.json["Email Body Markdown"];

let plainText = markdownContent;

// 1. Remove Markdown images completely
plainText = plainText.replace(/!\[.*?\]\(.*?\)/g, '');

// 2. Simplify Markdown links - keep text, replace URL with '[link]'
plainText = plainText.replace(/\[(.*?)\]\(.*?\)/g, '$1 [link]');
// --- OR Remove [link] placeholder entirely if not needed ---
// plainText = plainText.replace(/\[(.*?)\]\(.*?\)/g, '$1');

// 3. Remove any remaining HTML-like tags
plainText = plainText.replace(/<[^>]*>/g, '');

// 4. Remove Markdown table separators and leading/trailing pipes from lines
plainText = plainText.replace(/^\||\|$/gm, ''); // Remove leading/trailing pipes per line
plainText = plainText.replace(/^\s*[-|:]+\s*$/gm, ''); // Remove lines that are just separators

// 5. Normalize and reduce whitespace (run this AFTER other removals)
plainText = plainText.replace(/(\s*\r?\n\s*){2,}/g, '\n\n'); // Collapse multiple blank lines
plainText = plainText.split('\n').map(line => line.trim()).filter(line => line.length > 0).join('\n'); // Trim lines, remove empty lines
plainText = plainText.trim(); // Trim whole string

// 6. Truncate (Keep this step last)
const MAX_CHARS = 4000; // Or your desired limit
if (plainText.length > MAX_CHARS) {
  plainText = plainText.substring(0, MAX_CHARS) + "... (truncated)";
}

// Return the more thoroughly sanitized text
return { sanitizedBody: plainText };
