// 1. Extract and normalize order data
const payload = items[0].json;
const order = payload.order || payload.body || payload;
const lineItems = Array.isArray(order.line_items) ? order.line_items : [];
const customer = order.customer || {};
const shippingAddress = order.shipping_address || {};
const billingAddress = order.billing_address || {};

// 2. Get financial totals (order-level)
const subtotal = order.subtotal_price || order.current_subtotal_price_set?.amount || '0.00';
const shippingTotal = parseFloat(order.shipping_lines?.[0]?.price || order.current_shipping_price_set?.amount || 0);
const taxTotal = parseFloat(order.total_tax || '0.00');
const discountsTotal = order.total_discounts || '0.00';
const grandTotal = parseFloat(order.total_price || order.current_total_price_set?.amount || '0.00');
const currency = order.currency || 'USD';

// 3. Calculate total line item value
const totalLineItemValue = lineItems.reduce((sum, item) => {
  return sum + (parseFloat(item.price || 0) * item.quantity);
}, 0);

// 4. Find only taxable items
const taxableItems = lineItems.filter(item => item.tax_lines && item.tax_lines.length > 0);
const taxableTotal = taxableItems.reduce((sum, item) => {
  return sum + (parseFloat(item.price || 0) * item.quantity);
}, 0);

// 5. Clean formatted billing address (in 1 column)
const billingFullAddress = [
  billingAddress.name || `${customer.first_name || ""} ${customer.last_name || ""}`.trim(),
  billingAddress.phone,
  order.email || customer.email,
  billingAddress.address1,
  billingAddress.city,
  billingAddress.province,
  billingAddress.zip,
  billingAddress.country
].filter(Boolean).join(", ");

// 6. Build order-level base data
const orderBase = {
  "Order ID": order.id || "",
  "Order Name": order.name || "",
  "Order Number": order.order_number || "",
  "Order Created At": order.created_at || "",
  "Order Status URL": order.order_status_url || "",
  "Financial Status": order.financial_status || "",
  "Payment Gateway": order.gateway || "",
  "Currency": currency,
  "Subtotal": subtotal,
  "Grand Total": grandTotal.toFixed(2),
  "Total Tax": taxTotal.toFixed(2),
  "Shipping Total": shippingTotal.toFixed(2),
  "Discount Total": discountsTotal,

  "Customer Email": order.email || customer.email || "",
  "Customer Name": `${customer.first_name || ""} ${customer.last_name || ""}`.trim(),
  "Customer Phone": customer.phone || "",
  "Shipping City": shippingAddress.city || "",
  "Shipping Country": shippingAddress.country || "",
  "Shipping Phone": shippingAddress.phone || "",
  "Billing Address": billingFullAddress
};

// 7. Final output: one row per variant
const output = lineItems.map(item => {
  const lineTotal = parseFloat(item.price || 0) * item.quantity;
  const orderWeight = totalLineItemValue > 0 ? lineTotal / totalLineItemValue : 0;

  const isTaxable = item.tax_lines && item.tax_lines.length > 0;
  const taxWeight = isTaxable && taxableTotal > 0 ? lineTotal / taxableTotal : 0;

  const [color = "", size = "", material = ""] = (item.variant_title || "").split(" / ").concat(["", "", ""]).slice(0, 3);

  return {
    json: {
      ...orderBase,

      // Product Info
      "Product Name": item.title || "",
      "Variant Title": item.variant_title || "",
      "Color": color,
      "Size": size,
      "Material": material,
      "Quantity": item.quantity || 0,
      "Unit Price": parseFloat(item.price || 0).toFixed(2),
      "Line Total": lineTotal.toFixed(2),
      "Product ID": item.product_id || "",
      "Variant ID": item.variant_id || "",
      "SKU": item.sku || "",
      "Vendor": item.vendor || "",
      "Requires Shipping": item.requires_shipping ? "Yes" : "No",
      "Confirmation Number": order.confirmation_number || "",
      "Admin GraphQL API ID": item.admin_graphql_api_id || "",

      // Allocated values
      "Allocated Tax": (taxTotal * taxWeight).toFixed(2),
      "Allocated Shipping": (shippingTotal * orderWeight).toFixed(2),
      "Item % of Order": (orderWeight * 100).toFixed(2) + "%",

      // Clarity
      "Is Taxable": isTaxable ? "Yes" : "No"
    }
  };
});

return output;

