// Corrected Shopify Order Data Processor with Auto-Increment
// Input: Receives data from previous node containing row_number and S No
// Output: Increments both IDs while preserving all other data

// 1. Get the input data from previous node
const inputData = items[0].json;

// 2. Extract current row number and S No (handling string or number types)
const currentRowNumber = parseInt(inputData.row_number) || 0;
const currentSNo = parseInt(inputData["S No"]) || 0;

// 3. Increment both values
const newRowNumber = currentRowNumber + 1;
const newSNo = currentSNo + 1;

// 4. Create new data object with incremented values
const outputData = {
    ...inputData,  // Preserve all existing data

    // Update the incrementing fields (maintaining original field names)
    row_number: newRowNumber.toString(), // Keep as string if original was string
    "S No": newSNo.toString(), // Keep field name with space

    // Add processing metadata (optional)
    _processed: {
        previous_row: currentRowNumber,
        previous_sno: currentSNo,
        processed_at: new Date().toISOString()
    }
};

// 5. Return the updated data
return [{
    json: outputData
}];
