// Combine all items into one content block
let allContent = '';



for (const item of $input.all()) {
  const originalFrom = `${item.json.headers.from}`;
  const fromPrefix = "From: ";
  let trancatedFrom;

  if (originalFrom.startsWith(fromPrefix)) {
    trancatedFrom = originalFrom.substring(fromPrefix.length);
  } else {
    trancatedFrom = originalFrom;
  }
  // Create H2 heading from subject
  const heading = `<h3>${item.json.subject} [${trancatedFrom}]</h3>`;

  // Add heading and body to combined content
  allContent += heading + `\n\n${item.json.output.text}\n\n`;
}

// Return a single item with all combined content
return [{
  json: {
    combinedContent: allContent.trim(),
    itemCount: $input.all().length
  }
}];
