// Define the retry count (coming from a previous node or set manually)
const retryCount = $json["retryCount"] || 0;  // If not present, default to 0
const maxRetries = 5;  // Define the maximum number of retries
const initialDelay = 1;  // Initial delay in seconds (1 second)

// If the retry count is less than the max retries, calculate the delay
if (retryCount < maxRetries) {
    const currentDelayInSeconds = initialDelay * Math.pow(2, retryCount);  // Exponential backoff delay in seconds

    // Log the delay time for debugging
    console.log(`Waiting for ${currentDelayInSeconds} seconds before retry...`);

    return {
        json: {
            retryCount: retryCount + 1,  // Increment retry count
            waitTimeInSeconds: currentDelayInSeconds, // Pass the delay time in seconds
            status: 'retrying',
        }
    };
} else {
    // If max retries are exceeded, return a failure response
    return {
        json: {
            error: 'Max retries exceeded',
            retryCount: retryCount,
            status: 'failed'
        }
    };
}

