const places = items[0]?.json?.body?.places || [];

// Create an output array to hold each place as a separate item
let output = [];

if (places.length > 0) {
  for (let i = 0; i < places.length; i++) {
    // For each place, push a new item into the output array
    output.push({
      json: {
        place: places[i], // The individual place object
        otherData: items[0].json.otherData || null  // Include other data or default to null
      }
    });
  }
} else {
  // Log an error or handle the case where places array is empty or undefined
  console.log('Places array is empty or undefined.');
}

// Return the output array, so each place becomes its own item
return output;

