parameters.jsonBody:
={
  "contents": [{
    "parts": [
      {
        "text": "Read and analyze the legislative bill in the attached PDF strictly from an animal rights and anti-exploitation perspective. Evaluate whether the bill directly or indirectly harms or benefits animals, including wildlife, farmed animals, aquatic animals, or habitats. Treat all forms of animal use, exploitation, or commodification (such as fisheries, hunting, or industrial farming) as harmful to animal welfare, even if presented as sustainable or economically beneficial. Return a JSON object that matches the response_json_schema."
      },
      {
        "inline_data": {
          "mime_type": "application/pdf",
          "data": "{{ $json.data }}"
        }
      }
    ]
  }],
  "generationConfig": {
    "response_mime_type": "application/json",
    "response_json_schema": {
      "type": "object",
      "properties": {
        "bill_title": { 
          "type": "string", 
          "description": "Title or short identifier of the bill." 
        },
        "summary": { 
          "type": "string", 
          "description": "Plain-language summary of the bill with a critical animal rights analysis, highlighting any harm or benefits to animals or ecosystems." 
        },
        "animal_welfare_score": { 
          "type": "number", 
          "format": "float", 
          "minimum": 0, 
          "maximum": 1, 
          "description": "0 = extremely harmful or exploitative toward animals, 1 = strongly protective of animal rights." 
        },
        "relevance_score": { 
          "type": "number", 
          "format": "float", 
          "minimum": 0, 
          "maximum": 1, 
          "description": "0 = unrelated to animals, 1 = entirely about animal welfare or exploitation." 
        },
        "action_priority": { 
          "type": "number", 
          "format": "float", 
          "minimum": 0, 
          "maximum": 1, 
          "description": "0 = no meaningful action needed, 1 = immediate and critical action required by activists." 
        },
        "stance": { 
          "type": "string", 
          "enum": ["support", "oppose", "monitor"], 
          "description": "Recommended stance for animal activists (e.g., oppose if the bill promotes exploitation)." 
        },
        "key_points_for_action": { 
          "type": "array", 
          "items": { "type": "string" }, 
          "description": "Main points activists should emphasize in submissions or campaigns, highlighting animal rights concerns." 
        }
      },
      "required": [
        "bill_title",
        "summary",
        "animal_welfare_score",
        "relevance_score",
        "action_priority",
        "stance",
        "key_points_for_action"
      ],
      "propertyOrdering": [
        "bill_title",
        "summary",
        "animal_welfare_score",
        "relevance_score",
        "action_priority",
        "stance",
        "key_points_for_action"
      ]
    }
  }
}

