const base64String = $input.first().json.image_base64;
console.log(base64String);
// If it includes a prefix like 'data:image/jpeg;base64,', split it
const cleanedBase64 = base64String.includes(",")
  ? base64String.split(",")[1]
  : base64String;

return [
  {
    binary: {
      data: {
        data: Buffer.from(cleanedBase64, 'base64'),
        mimeType: 'image/jpeg', // or image/png depending on the format
        fileName: 'output.jpg'
      }
    }
  }
];
