parameters.inputSchema:
{
	"type": "object",
    "additionalProperties": false,
    "required": [
      "jobName",
      "jobType", 
      "jobDetailUrl", 
      "price", 
      "descriptionSnippet", 
      "country", 
      "paymentVerified",
      "clientRating",
      "amountSpent",
      "tags",
      "postedAt",
      "experienceLevel"
    ],
	"properties": {
        "jobName": {
          "type": "string"
        },
		"jobType": {
			"enum": ["Hourly", "Fixed"]
		},
        "price": {
          "type": "string"
        },
        "jobDetailUrl": {
          "type": "string"
        },
        "descriptionSnippet": {
          "type": "string",
          "description": "The description of the job before the 'more' link"
        },
      "country": {
        "type": "string"
      },
      "paymentVerified": {
        "type": "boolean"
      },
      "clientRating": {
        "type": "number"
      },
      "amountSpent": {
        "type": "string"
      },
      "tags": {
        "type": "array",
        "description": "List of categories with a link typically found below the country. Just include the tag name, not the url.",
        "items": {
          "type": "string"
        }
      },
      "postedAt": {
        "type": "string"
      },
      "experienceLevel": {
        "enum": ["Entry", "Intermediate", "Expert"]
      }
	}
}
