// Sample input data from previous node
const items = $json["output"];

// Emit each review as a single item
return items.map(review => {
  return {
    json: {
      title: review.title,
      url: "https://www.theverge.com" + review.url,
      release_date: review.release_date,
      summary: review.summary
    }
  };
});

