// Content Strategy Configuration & Trend Monitoring Setup
// Created: 25/7/2025

const contentConfig = {
  industries: [
    'artificial intelligence',
    'digital marketing',
    'technology trends',
    'social media marketing',
    'content marketing',
    'SEO and search marketing',
    'e-commerce trends',
    'startup ecosystem',
    'remote work trends',
    'cybersecurity'
  ],
  contentTypes: [
    'blog posts',
    'social media content',
    'video scripts',
    'infographics',
    'podcasts',
    'webinars',
    'case studies',
    'whitepapers',
    'newsletters',
    'tutorials'
  ],
  platforms: {
    'LinkedIn': 'professional audience, B2B content',
    'Twitter': 'real-time updates, industry news',
    'Instagram': 'visual content, behind-the-scenes',
    'YouTube': 'educational content, tutorials',
    'TikTok': 'trending topics, quick tips',
    'Facebook': 'community engagement, discussions',
    'Pinterest': 'visual inspiration, how-to guides',
    'Reddit': 'community discussions, AMAs'
  },
  contentCalendar: {
    posting_frequency: {
      'blog': '3 times per week',
      'social': 'daily',
      'video': 'twice per week',
      'newsletter': 'weekly'
    },
    content_pillars: [
      'Industry Insights & Trends',
      'Educational & How-To Content',
      'Company News & Updates',
      'User-Generated Content',
      'Behind-the-Scenes',
      'Thought Leadership'
    ],
    seasonal_events: [
      'Q4 2025 Planning',
      'Back-to-School Season',
      'Holiday Marketing',
      'New Year Resolutions',
      'Industry Conference Season'
    ]
  },
  trendAnalysis: {
    timeframes: ['24h', '7d', '30d'],
    engagement_thresholds: {
      'viral': 100000,
      'trending': 10000,
      'emerging': 1000
    },
    sentiment_analysis: true,
    competitor_monitoring: true,
    influencer_tracking: true
  }
};

const today = new Date('2025-07-25');
const trendQueries = [];

Object.keys(contentConfig.platforms).forEach(platform => {
  contentConfig.industries.forEach(industry => {
    trendQueries.push({
      platform: platform,
      industry: industry,
      query: `${industry} trends ${platform.toLowerCase()} 2025`,
      search_url: generateSearchURL(platform, industry),
      content_focus: contentConfig.platforms[platform]
    });
  });
});

const keywordQueries = contentConfig.industries.map(industry => ({
  industry: industry,
  query: `${industry} keywords trending july 2025`,
  search_url: `https://trends.google.com/trends/explore?q=${encodeURIComponent(industry)}&date=now%207-d`,
  analysis_type: 'keyword_volume'
}));

const competitorQueries = [
  'top content marketing campaigns 2025',
  'viral marketing strategies july 2025',
  'best performing content types 2025',
  'social media engagement trends 2025'
].map(query => ({
  query: query,
  search_url: `https://www.buzzsumo.com/search?q=${encodeURIComponent(query)}`,
  analysis_type: 'competitor_content'
}));

function generateSearchURL(platform, industry) {
  const baseUrls = {
    'LinkedIn': `https://www.linkedin.com/search/results/content/?keywords=${encodeURIComponent(industry + ' trends')}`,
    'Twitter': `https://twitter.com/search?q=${encodeURIComponent(industry + ' trends')}&src=trend_click`,
    'Instagram': `https://www.instagram.com/explore/tags/${encodeURIComponent(industry.replace(/\s+/g, ''))}/`,
    'YouTube': `https://www.youtube.com/results?search_query=${encodeURIComponent(industry + ' trends 2025')}`,
    'TikTok': `https://www.tiktok.com/search?q=${encodeURIComponent(industry)}`,
    'Reddit': `https://www.reddit.com/search/?q=${encodeURIComponent(industry + ' trends')}&type=link&sort=hot`
  };
  return baseUrls[platform] || `https://www.google.com/search?q=${encodeURIComponent(industry + ' ' + platform + ' trends')}`;
}

const sessionId = `trend_monitoring_${Date.now()}`;
const timestamp = today.toISOString();

return [{
  json: {
    sessionId: sessionId,
    timestamp: timestamp,
    date: '2025-07-25',
    config: contentConfig,
    trendQueries: trendQueries,
    keywordQueries: keywordQueries,
    competitorQueries: competitorQueries,
    totalQueries: trendQueries.length + keywordQueries.length + competitorQueries.length,
    analysisMode: 'comprehensive'
  }
}];

const items = $input.all();
const configData = items[0].json;
const batchData = $('Split Trend Queries').item.json;

const allQueries = [
  ...configData.trendQueries,
  ...configData.keywordQueries,
  ...configData.competitorQueries
];

const currentQuery = allQueries[batchData.index];

return [{
  json: {
    ...configData,
    currentQuery: currentQuery,
    batchIndex: batchData.index,
    totalBatches: allQueries.length,
    queryType: currentQuery.analysis_type || 'trend_analysis'
  }
}];

const allData = $input.all();
const configData = allData[0].json;
const socialData = allData[1]?.json?.result || {};
const trendsData = allData[2]?.json?.result || {};
const viralData = allData[3]?.json?.result || {};
const redditData = allData[4]?.json?.result || {};

const trendAnalyzer = {
  calculateTrendMomentum(engagementScore, growthRate, timeframe) {
    const engagement = parseInt(engagementScore) || 0;
    const growth = parseFloat(growthRate?.replace(/[^0-9.-]/g, '')) || 0;
    const timeMultiplier = timeframe === '24h' ? 3 : timeframe === '7d' ? 2 : 1;
    return Math.min(100, (engagement / 1000 + growth + timeMultiplier * 10));
  },

  identifyContentGaps(socialTrends, searchTrends, discussions) {
    const gaps = [];
    const trendingTopics = [...(socialTrends.trending_topics || []), ...(searchTrends.trending_searches || [])];
    const existingContent = viralData.viral_content || [];

    trendingTopics.forEach(trend => {
      const hasContent = existingContent.some(content => 
        content.title?.toLowerCase().includes(trend.topic?.toLowerCase() || trend.keyword?.toLowerCase())
      );

      if (!hasContent && trend.engagement_score > 10000) {
        gaps.push({
          opportunity_type: 'trending_gap',
          topic: trend.topic || trend.keyword,
          potential_reach: trend.engagement_score || trend.search_volume,
          urgency_score: this.calculateUrgencyScore(trend),
          content_suggestion: this.generateContentSuggestion(trend),
          target_platforms: this.recommendPlatforms(trend)
        });
      }
    });
    return gaps;
  },

  calculateUrgencyScore(trend) {
    let score = 0;
    if (trend.trend_velocity === 'rising' || trend.growth === 'breakout') score += 40;
    if (trend.engagement_score > 50000) score += 30;
    if (trend.sentiment === 'positive') score += 20;
    if (trend.seasonality && trend.seasonality.includes('peak')) score += 10;
    return Math.min(100, score);
  },

  generateContentSuggestion(trend) {
    const topic = trend.topic || trend.keyword;
    const suggestions = [];

    if (trend.content_type === 'video' || trend.platform === 'YouTube') {
      suggestions.push(`Video tutorial: "Ultimate Guide to ${topic} in 2025"`);
      suggestions.push(`Short-form video: "5 ${topic} Tips That Will Blow Your Mind"`);
    }

    if (trend.platform === 'LinkedIn' || trend.demographics?.includes('professional')) {
      suggestions.push(`LinkedIn article: "How ${topic} is Transforming Business in 2025"`);
      suggestions.push(`Industry insight post: "${topic} Trends Every Professional Should Know"`);
    }

    if (trend.hashtags?.length > 0) {
      suggestions.push(`Social media series using ${trend.hashtags.join(', ')}`);
    }

    if (trend.related_queries) {
      trend.related_queries.forEach(query => {
        suggestions.push(`Blog post: "${query}" - Complete Answer Guide`);
      });
    }

    return suggestions.slice(0, 3);
  },

  recommendPlatforms(trend) {
    const platforms = [];

    if (trend.demographics?.includes('professional') || trend.platform === 'LinkedIn') {
      platforms.push('LinkedIn');
    }

    if (trend.content_type === 'video' || trend.platform === 'YouTube') {
      platforms.push('YouTube', 'TikTok');
    }

    if (trend.trend_velocity === 'rising') {
      platforms.push('Twitter', 'Instagram Stories');
    }

    if (trend.engagement_score > 100000) {
      platforms.push('Facebook', 'Instagram');
    }

    return [...new Set(platforms)];
  }
};

const trendAnalysis = {
  session_id: configData.sessionId,
  analysis_date: configData.date,
  timestamp: new Date().toISOString(),

  trending_topics: [
    ...(socialData.trending_topics || []),
    ...(trendsData.trending_searches || []).map(item => ({
      topic: item.keyword,
      engagement_score: parseInt(item.search_volume?.replace(/[^0-9]/g, '')) || 0,
      trend_velocity: item.growth === 'breakout' ? 'rising' : 'steady',
      source: 'Google Trends'
    }))
  ],

  viral_patterns: {
    top_performing_content: viralData.viral_content || [],
    successful_patterns: viralData.content_patterns || [],
    optimal_timing: viralData.optimal_posting_times || {},
    hashtag_performance: viralData.hashtag_performance || []
  },

  community_insights: {
    reddit_discussions: redditData.hot_discussions || [],
    pain_points: redditData.community_insights?.content_gaps || [],
    user_questions: redditData.hot_discussions?.flatMap(d => d.emerging_questions || []) || []
  },

  content_opportunities: [],
  content_gaps: [],
  recommended_actions: []
};

trendAnalysis.content_opportunities = [
  ...(socialData.content_opportunities || []),
  ...(trendsData.content_suggestions || []),
  ...(redditData.content_opportunities || [])
];

trendAnalysis.content_gaps = trendAnalyzer.identifyContentGaps(
  socialData,
  trendsData,
  redditData.hot_discussions || []
);

trendAnalysis.recommended_actions = [
  {
    priority: 'high',
    action: 'Create content for trending gaps',
    details: trendAnalysis.content_gaps.filter(gap => gap.urgency_score > 70),
    timeline: 'within 24 hours'
  },
  {
    priority: 'medium',
    action: 'Optimize posting schedule',
    details: trendAnalysis.viral_patterns.optimal_timing,
    timeline: 'next week'
  },
  {
    priority: 'low',
    action: 'Monitor emerging trends',
    details: trendAnalysis.trending_topics.filter(topic => topic.trend_velocity === 'rising'),
    timeline: 'ongoing'
  }
];

const avgEngagement = trendAnalysis.trending_topics.reduce((sum, topic) => 
  sum + (topic.engagement_score || 0), 0) / trendAnalysis.trending_topics.length;

trendAnalysis.trend_health_score = Math.min(100, avgEngagement / 1000);
trendAnalysis.total_opportunities = trendAnalysis.content_opportunities.length + trendAnalysis.content_gaps.length;

return [{ json: trendAnalysis }];

