parameters.inputSchema:
{
  "$schema": "http://json-schema.org/draft-07/schema#",
  "title": "Vlog Storyboard",
  "description": "A structured storyboard for a short vlog episode.",
  "type": "object",
  "required": [
    "vlogTitle",
    "concept",
    "characterName",
    "scenes"
  ],
  "properties": {
    "vlogTitle": {
      "description": "A creative and fun title for the vlog episode.",
      "type": "string"
    },
    "concept": {
      "description": "The user-provided concept for the vlog.",
      "type": "string"
    },
    "characterName": {
      "description": "The name of the protagonist.",
      "type": "string",
      "const": "Sam"
    },
    "scenes": {
      "description": "An array of 10 scenes that form the storyboard.",
      "type": "array",
      "minItems": 8,
      "maxItems": 8,
      "items": {
        "type": "object",
        "required": [
          "sceneNumber",
          "timestamp",
          "durationSeconds",
          "narrative"
        ],
        "properties": {
          "sceneNumber": {
            "description": "The sequential number of the scene.",
            "type": "integer",
            "minimum": 1,
            "maximum": 8
          },
          "timestamp": {
            "description": "The time range of the scene in the vlog (e.g., '0:00-0:08').",
            "type": "string",
            "pattern": "^\\d{1,2}:\\d{2}-\\d{1,2}:\\d{2}$"
          },
          "durationSeconds": {
            "description": "The duration of the scene in seconds.",
            "type": "integer",
            "const": 8
          },
          "narrative": {
            "description": "A single, descriptive paragraph weaving together the visual action, Sam's expressions, and his spoken dialogue.",
            "type": "string"
          }
        }
      }
    }
  }
}
