parameters.jsCode:
// Group 24 image items into 12 scenes (2 per scene, by Part 1 and 2)
const groupedByScene = {};

// Loop over each image entry
for (const item of items) {
  const scene = item.json['Scene Number'];
  const url = item.json['Image URL'];

  if (!groupedByScene[scene]) {
    groupedByScene[scene] = {
      sceneNumber: scene,
      imageUrls: [],
      main: item.json.Main?.[0] || null
    };
  }

  groupedByScene[scene].imageUrls.push(url);
}

// Return one item per scene
return Object.values(groupedByScene).map(data => ({ json: data }));
